        <Route path="/premium-content" element={isPremium ? <PremiumContent /> : <navigate to="/" />} />

      </Routes>
    </Router>
  );
}


async function isUserPremium() {
  try {
      const response = await fetch('/api/users/is-premium');
      const data = await response.json();
      return data.premium;
  } catch (error) {
      return false;
  }
}


export default App;
